//отслеживание скролла при котором меняется класс навбара скролла
let servicesList = document.querySelector('.services__list');
servicesList.addEventListener('scroll', function () {
	let scrollL = servicesList.scrollLeft;
	let navAll = document.querySelectorAll('.label');
	if (scrollL < 270) {
		let navAll = document.querySelectorAll('.label');
		navAll.forEach(e => {
			e.classList.remove('show');
		})
		let nav = document.querySelectorAll('.label')[0];
		nav.classList.add('show');
	}
	else if (scrollL >= 270 && scrollL < 550) {
		navAll.forEach(e => {
			e.classList.remove('show');
		})
		let nav = navAll[1];
		nav.classList.add('show');
	}
	else if (scrollL > 550 && scrollL < 850) {
		navAll.forEach(e => {
			e.classList.remove('show');
		})
		let nav = navAll[2];
		nav.classList.add('show');
	}
	else if (scrollL > 850 && scrollL < 1150) {
		navAll.forEach(e => {
			e.classList.remove('show');
		})
		let nav = navAll[3];
		nav.classList.add('show');
	}
	else if (scrollL > 1150 && scrollL < 1450) {
		navAll.forEach(e => {
			e.classList.remove('show');
		})
		let nav = navAll[4];
		nav.classList.add('show');
	}
	else if (scrollL > 1450 && scrollL < 1700) {
		navAll.forEach(e => {
			e.classList.remove('show');
		})
		let nav = navAll[5];
		nav.classList.add('show');
	}
	else if (scrollL > 1700 && scrollL < 2000) {
		navAll.forEach(e => {
			e.classList.remove('show');
		})
		let nav = navAll[6];
		nav.classList.add('show');
	}
	else {
		navAll.forEach(e => {
			e.classList.remove('show');
		})
		let nav = navAll[7];
		nav.classList.add('show');
	}
});

//снятие класса show со всех элементов с классом label и установка на тот по которому производится клик
let scrollClick = document.querySelectorAll('.label');
scrollClick.forEach(label => {
	label.addEventListener('click', () => {
		let labels = document.querySelectorAll('.label');
		for (let i = 0; i < 8; i++) {
			labels[i].classList.remove('show');
		}
		label.classList.add('show');
	})
})

