//скролл вперёд/назад
function scrollBtn(num) {
	let servicesList = document.querySelector('.services__list');
	let item = document.querySelector('.services__list__item').clientWidth;
	item += 30;
	switch (num) {
		case 1: {
			servicesList.scrollBy({ left: item, top: 0, behavior: 'smooth' });
			break;
		}
		case 2: {
			servicesList.scrollBy({ left: -item, top: 0, behavior: 'smooth' });
			break;
		}
	}
}

(function () {
	let speed = 1.3;

	let scroll = document.querySelector('.services__list');

	let left = 0;
	let drag = false;
	let coorX = 0;

	scroll.addEventListener('mousedown', function (e) {
		drag = true;
		coorX = e.pageX - this.offsetLeft;
	});
	document.addEventListener('mouseup', function () {
		drag = false;
		left = scroll.scrollLeft;
	});
	scroll.addEventListener('mousemove', function (e) {
		if (drag) {
			this.scrollLeft = left - (e.pageX - this.offsetLeft - coorX) * speed;
		}
	});
})();